			sel = this.sbTradeTypes.getSelection();
			if (sel != null && sel.length > 0) {
				filterTypeId = sel[0].getModel();
			}	

			var filterTransportTypeId = -1;
			sel = this.sbTransportTypes.getSelection();
			if (sel != null && sel.length > 0) {
				filterTransportTypeId = sel[0].getModel();
			}

