											commandManager.sendCommand("OrderUnits", request, null, function() { });
											var tcid = Number(String(data[ii][3]).replace(',', ''));
											var cx = tcid & 0xFFFF;
											var cy = tcid >> 16;
											dist = Math.max(dist, Math.sqrt((x - cx) * (x - cx) + (y - cy) * (y - cy)));
