					formatNumber : function(str) {
						var num = String(str).replace(/\,/g, '');
						var pos = num.indexOf('.');
						if (pos >= 0) {
							num = num.substring(0, pos)
						};
						if (num.length == 0 || isNaN(num)) {
							return "";
						}
						var val = "";
						for (var i = 0, numLen = num.length; i < numLen; ++i) {
							if (val.length > 0 && (((num.length - i) % 3) == 0)) {
								val = val + ",";
							}
							val += num.substring(i, i + 1);
						}
						return val;
					},
